select  * from information_schema.columns where table_catalog = 'NBCONFIG'

select * from (
select distinct  'select top 100 ' +' '+ ('''['+ table_schema +']'+ '.' +'['+ replace(replace(table_name,' ','_'),'-','_') +']'' as TableName_'+  table_schema + '_' + replace(replace(table_name,' ','_'),'-','_') +','  )  +  STRING_AGG(CONVERT(NVARCHAR(max), ('['+column_name+']')),',') + ' from ' + '['+ table_schema +']'+ '.' +'['+ table_name +']' AS selectList from information_schema.columns 
where table_name in ( select distinct table_name from information_schema.columns) and table_name in (select top 1000 name from sys.tables order by create_date desc)
--and table_schema like 'elig'
group by table_schema, table_name
) a

select distinct 'select '+ ''''+table_schema+'.'+ table_name +''''+ 'as TableName' +','+ 'count(*) as RecordCount from ' + '['+ table_schema +']'+ '.' +'['+ table_name +']' + ' Union ' from information_schema.columns


